
public class String_Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "Nagpur";
		System.out.println(str);
		int len = str.length();
		System.out.println("Length of the string str is "+len);
		String upper_str = str.toUpperCase();
		System.out.println("Upper Case String is : "+upper_str);
		String lower_str = str.toLowerCase();
		System.out.println("Lower Case String is : "+lower_str);
	}

}
